import React from "react";
import Categories from "../Categories/Categories";
import SearchBar from "../SearchBar/SearchBar";
import img1 from '../img/destacado_1.png';
import img2 from '../img/destacado_2.jpg';

const mainTopic = [
    {
        img: img1,
        title: 'ABBA',
        des1: 'POPO es un grupo sueco de música pop, integrado por Agnetha Fältskog, Björn Ulvaeus, Benny Andersson y Anni-Frid «Frida» Lyngstad.',
        des2: 'El nombre «ABBA» es un acrónimo formado por las primeras letras del nombre de cada miembro (Agnetha, Björn, Benny, Anni-Frid).'
    },
    {
        img: img2,
        title: 'Fuji Rojo',
        des1: 'Fuji rojo —también conocida según su nombre original (凱風快晴 Gaifū kaisei?), en español Viento del sur, cielo claro— es una estampa japonesa del pintor especialista en ukiyo-e, Katsushika Hokusai, que forma parte de la serie Treinta y seis vistas del monte Fuji.',
        des2: 'weiFue publicada entre 1829 y 1833, durante el Período Edo, y actualmente es propiedad de varios museos, incluyendo el Museo Británico en Londres, el Museo Metropolitano de Arte de Nueva York, y el Museo de Arte de Indianápolis.'
    }
];

function Main() {

    const mainTopicGenerate = mainTopic.map((topic, index) => {
        return (
            <div className="principal-section">
                    <img className="principal-img" src={topic.img} alt={topic.img} />
                    <div className="principal-text">
                        <p>{topic.des1}</p>
                        <p>{topic.des2}</p>
                    </div>
                </div>
        );
    });

    return (
        <main id="main">

        <SearchBar />

        <Categories />

        <section id="main-principal">
            <h1 className="title">Articulos Destacados</h1>
            <div id="principal-grid">
                {mainTopicGenerate}
            </div>
        </section>


    </main>
    );
}

export default Main;
