import React from "react";
import { useState, useEffect  } from "react";
import { FaBars, FaPlus, FaTrash, FaFlag } from 'react-icons/fa';


function ToolsMenu() {

    const [toolsValue, setToolsValue] = useState('menu-tools');

    const toolsBtnOver = () => {
        setToolsValue('menu-tools active');
    }

    const toolsBtnLeave = () => {
        setToolsValue('menu-tools');
    }

    const [newTopic, setNewTopic] = useState(null);
    const [bodyScroll, setBodyScroll] = useState('auto');
    const [toolAction, setToolAction] = useState([]);
    const toolsLi = document.querySelectorAll('.menu-tools li');

    const actionClick = action => {
            console.log(action.target.outerText);
            if(action.innerText === 'Nuevo Topic'){
                console.log('Nuevo')
            }
    }

    return (

        <div id="header-tools">
                <i onMouseOver={toolsBtnOver} className="fas fa-bars"><FaBars /></i>
                <ul onMouseLeave={toolsBtnLeave} id="menu-tools" className={toolsValue}>
                    <li onClick={actionClick}><i className="fa-solid fa-plus"></i><FaPlus />Nuevo Topic</li>
                    <li><i className="fa-solid fa-pen-to-square"></i>Edit</li>
                    <li><i className="fa-solid fa-trash"></i><FaTrash />Delete</li>
                    <li><i className="fa-solid fa-flag"></i><FaFlag />Report</li>
                </ul>
            </div>

    );
}

export const toolActions = document.querySelectorAll('#menu-tools li');

export default ToolsMenu;
