import React from "react";
import { useState, useEffect  } from "react";
import { FaBars, FaPlus, FaTrash, FaFlag } from 'react-icons/fa';


function ToolsMenu() {

    const toolsMenu = document.querySelector('#menu-tools');
    const toolActions = document.querySelectorAll('.menu-tools li');

    const handleToolsBtnMouseOver = () => {
        toolsMenu.classList.add('active');
    }

    const handleToolsMenuMouseLeave = () => {
        toolsMenu.classList.remove('active');
    }
    
    const [newTools, setNewTools] = useState(false);
    const [bodyOverFlow, setBodyOverFlow] = useState(false);
    

    return (

        <div id="header-tools">
                <i onMouseOver={handleToolsBtnMouseOver} className="fas fa-bars"><FaBars /></i>
                <ul onMouseLeave={handleToolsMenuMouseLeave} id="menu-tools" className="menu-tools">
                    <li><i className="fa-solid fa-plus"></i><FaPlus />Nuevo Topic</li>
                    <li><i className="fa-solid fa-pen-to-square"></i>Edit</li>
                    <li><i className="fa-solid fa-trash"></i><FaTrash />Delete</li>
                    <li><i className="fa-solid fa-flag"></i><FaFlag />Report</li>
                </ul>
            </div>

    );
}

export default ToolsMenu;
export const toolActions = ["add" ,"edit", "remove", "report"]
export function toolsEvents(newTopic, body) {

    toolActions.forEach((action) => {
        action.addEventListener('click', () => {
            if (toolActions[0] === action) {
                newTopic.style.display = "block";
                body.style.overflow = "hidden";
                console.log("add");
            } else if (toolActions[1] === action) {
                console.log("edit");
            } else if (toolActions[2] === action) {
                console.log("erase");
            } else if (toolActions[3] === action) {
                console.log("report");
            }
        });
    });

}
