import React from "react";
import { useState, useEffect } from "react";
import { toolActions, toolsEvents } from "./tools_menu";


function NewTopic() {

    const [newWindow, setNewWindow] = useState(false);

    useEffect(() => {
        const newTopic = document.querySelector('.new-topic');
        const body = document.querySelector('body');

        toolsEvents(newTopic, body);

        return () => {
            toolActions.forEach((action) => {
                action.removeEventListener('click', () => {});
            });
        }

    });

    const [title, setTitle] = useState("");
    const [description, setDescription] = useState("");
    const [author, setAuthor] = useState("");
    const [imgThumb, setImgThum] = useState("");
    const [selectedFile, setSelectedFile] = useState(null);

    return(
        <div className="new-topic">
        <div className="new-topic-content">
            <h1>Crear un nuevo articulo</h1>
            <form className="new-form">
                <label htmlFor="new-name">Titulo</label>
                <input type="text" id="new-name" name="new-name" value={title} />
                <label htmlFor="new-content">Descripcion del articulo</label>
                <textarea id="new-content" name="new-content" value={description}></textarea>
                <label htmlFor="new-author">Autor</label>
                <input type="text" id="new-author" name="new-author" value={author} />
                <img id="new-image-preview" src="" />
                <input type="file" id="new-image" name="new-image" value={selectedFile} />
                <select>
                    <option>Musica</option>
                    <option>Entretenimiento</option>
                    <option>Religion</option>
                    <option>Politica</option>
                    <option>Deportes</option>
                    <option>Educacion</option>
                </select>
                <button>Crear Articulo</button>
                
            </form>

        </div>
        <span className="close-button">&times;</span>
    </div>
    );
}

export default NewTopic;
