import React from "react";
import { useState } from "react";

function Categories() {
    const [subValue, setSubValue] = useState("");

    function handleSubtitleMouseOver(e) {
        const index = e.currentTarget.getAttribute('data-index');
        const imgCategory = document.querySelectorAll('.category img')[index];
        const subtitle = document.querySelectorAll('.category .subtitle')[index];

        imgCategory.style.transform = "scale(1.15)";
        subtitle.style.opacity = "0.3";
        subtitle.style.cursor = "pointer";
        setSubValue(subtitle.innerHTML);
        subtitle.innerHTML = "";
    }

    function handleSubtitleMouseOut(e) {
        const index = e.currentTarget.getAttribute('data-index');
        const imgCategory = document.querySelectorAll('.category img')[index];
        const subtitle = document.querySelectorAll('.category .subtitle')[index];
    
        imgCategory.style.transform = "scale(1)";
        subtitle.style.opacity = "1";
        subtitle.innerHTML = subValue;
    }

    function handleDocumentMouseUp(e) {
        const searchBar = document.querySelector('#search-text');
        if(!searchBar.contains(e.target)) {
            searchBar.style.background = "#aaa";
        } else {
            searchBar.style.background = "#fff";
        }
    }

    return (
        <section id="main-category">
            <h1 class="title">Categorias</h1>
            <div id="category-grid">
                <div class="category b1">
                    <h3 class="subtitle" onMouseOver={handleSubtitleMouseOver}
                    onMouseOut={handleSubtitleMouseOut}>Deportes</h3><hr />
                    <img src="./img/deportes.jpg" alt="" />

                </div>

                <div class="category b2">
                    <h3 class="subtitle" onMouseOver={handleSubtitleMouseOver}
                    onMouseOut={handleSubtitleMouseOut}>Politica</h3><hr />
                    <img src="./img/politica.jfif" alt="" />
                </div>

                <div class="category b3">
                    <h3 class="subtitle" onMouseOver={handleSubtitleMouseOver}
                    onMouseOut={handleSubtitleMouseOut}>Religion</h3><hr />
                    <img src="./img/religion.jpg" alt="" />
                </div>

                <div class="category b4">
                    <h3 class="subtitle" onMouseOver={handleSubtitleMouseOver}
                    onMouseOut={handleSubtitleMouseOut}>Educaci√≥n</h3><hr />
                    <img src="./img/educacion.jfif" alt="" />
                </div>

                <div class="category b5">
                    <h3 class="subtitle" onMouseOver={handleSubtitleMouseOver}
                    onMouseOut={handleSubtitleMouseOut}>Entretenimiento</h3><hr />
                    <img src="./img/entretenimiento.jpg" alt="" />
                </div>

                <div class="category b6">
                    <h3 class="subtitle" onMouseOver={handleSubtitleMouseOver}
                    onMouseOut={handleSubtitleMouseOut}>Musica</h3><hr />
                    <img src="./img/musica.jpg" alt="" />
                </div>

            </div>

        </section>
    );

}

export default Categories;
